import React, { useState, useEffect } from "react";
import axios from "axios";




const Product = () => {
    const [categories, setCategories] = useState([]);
const [products, setProducts] = useState([]);
const [product, setProduct] = useState({
    product: "",
    price: "",
    categoryId: ""
  });

useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/product"
        );
        setProducts(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchCategories();
  }, []);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/category"
        );
        setCategories(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchCategories();
  }, []);

  const handleProductChange = (event) => {
    const { name, value } = event.target;
    setProduct({ ...product, [name]: value });
  };

  const handleCreateProduct = async (event) => {

    event.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/product", {
        method: "post",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
        //   category: category.category,
        }),
      });

      if (response.ok) {
        // Handle success, e.g., clear form fields or show a success message
        setProduct({
          product: "",
        });
      } else {
        // Handle error, e.g., display an error message
        console.error("Failed to create category");
      }
    } catch (error) {
      console.error(error);
    }
  };






  return (
    <>
      <div className="category-table">
        <div className="list-category" >
           <h3 className="list1">list of Products</h3>

                  {products.map((item, i) => (
                    <div className="list2">
                   <p key={i}>{item.product}</p>
                   <p> Price: {item.price} $</p>
                   </div>
                  ))}

        </div>
        <div className="subcat-form-container">
          <h1>Creat Category</h1>
          <form
            className="cat-edit-form2"
            onSubmit={handleCreateProduct}

          >
            <div className="username">

              <label className="About_username">Product name:</label> <br />
              <input
                className="subcat-edit-input"
                type="text"
                id="username"
                placeholder="Product name"
                name="product"
                value={product.product}
                onChange={handleProductChange}
              />
              </div>
              <div>
              <label className="About_username">Category name:</label>
              <select
                  id="category"
                  name="categoryTitle"
                  value={product.categoryId}
                  onChange={handleProductChange}
                >
                  <option value="">Select a category...</option>
                  {categories.map((category) => (

                    <option key={category._id} value={category._id}>
                      {category.category}
                    </option>

                  ))}

                </select>
            </div>
               <div className="username">
               <label className="About_username">Product Price:</label> <br />
              <input
                className="subcat-edit-input"
                type="text"
                id="username"
                placeholder="Price"
                name="price"
                value={product.price}
                onChange={handleProductChange}
              />
              </div>

            <button className="subcat-edit-button" type="submit">
             Create
            </button>
          </form>
        </div>
      </div>
    </>
  );
};

export default Product;
