              <input
                className="subcat-edit-input"
                type="text"
                id="username"
                placeholder="Category name"
                name="title"
                value={category.category}
                onChange={handleCategoryChange}
              />
            </div>
            <button className="subcat-edit-button" type="submit">
             Create
            </button>
          </form>
        </div>
      </div>
    </>
  );
};

export default Category;
