import React, { useState, useEffect, useRef } from "react";
import "./menuDetails.css";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";

function MenuDetails() {
  const columns = [
    { id: "images", label: "Products", minWidth: 100 },
    { id: "product", label: "Category", minWidth: 100 },
    { id: "edit", label: "Price", minWidth: 100 },
    { id: "edit", label: "Edit price", minWidth: 100 },
  ];

  const [menus, setMenus] = useState([]);

  useEffect(() => {
    const fetchMenus = async () => {
      try {
        const response = await axios.get("http://localhost:5000/menu");
        setMenus(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMenus();
  }, []);

  return (
    <>
      <div className="menuDetails">
        <h1>Menu Details</h1>
        <div>
          {menus.map((item, i ) =>(
         <>
           <h3 key={i} >{item.menu}</h3>
           <div>
<Paper
  sx={{
    width: "75%",
    overflow: "hidden",
    marginLeft: "auto",
    marginRight: "auto",
    border: "#0B486A solid 1px",
  }}
>
  <TableContainer sx={{ maxHeight: "600px" }}>
    <Table stickyHeader aria-label="sticky table">
      <TableHead>
        <TableRow>
          {columns.map((column) => (
            <TableCell
              key={column.id}
              align={column.align}
              style={{ minWidth: column.minWidth }}
            >
              {column.label}
            </TableCell>
          ))}
        </TableRow>
      </TableHead>
      <TableBody>
        {item.products?.map((product, i) => (
          <TableRow key={i}>
            <TableCell>{product.product}</TableCell>
            <TableCell>{product.categoryId.category}</TableCell>
            <TableCell>{product.price} $</TableCell>
            <TableCell>
              <input type= "text" className="price" />
              <button
                className="prodash-button"
                //   onClick={() => getProductById(product._id)}
              >
                Edit Price{" "}
              </button>
            </TableCell>
          </TableRow>
        ))}
      </TableBody>
    </Table>
  </TableContainer>
</Paper>
</div>






        </>
          ))}
        </div>
      </div>
    </>
  );
}

export default MenuDetails;
