import React, { useState, useEffect } from "react";
import axios from "axios";


const Category = () => {
const column = [
        { id: "title", label: "Category", minWidth: 100 },

      ];
const [categories, setCategories] = useState([]);

useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/category"
        );
        setCategories(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchCategories();
  }, []);




  return (
    <>
      <div className="category-table">
        <div >
          <Paper
            sx={{
              width: "90%",


              marginLeft: "auto",
              marginRight: "auto",
              border: "#0B486A solid 1px",
            }}
          >
            <TableContainer sx={{ maxHeight: "600px", }}>
              <Table stickyHeader aria-label="sticky table">
                <TableHead>
                  <TableRow>
                    {column.map((colum) => (
                      <TableCell
                        key={colum.id}
                        align={colum.align}
                        style={{ minWidth: colum.minWidth }}
                      >
                        {colum.label}
                      </TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {categories.map((category, i) => (
                    <TableRow key={i}>
                      <TableCell>{category.category}</TableCell>

                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Paper>
        </div>
        <div className="subcat-form-container">
          <h1>Creat Category</h1>
          <form
            className="cat-edit-form"
            // onSubmit={handleEditSubmitCategory}
            // ref={form}
          >
            <div className="username">
              <label className="About_username">Category name:</label> <br />
              <input
                className="subcat-edit-input"
                type="text"
                id="username"
                placeholder="Category name"
                name="title"
                // value={category.category}
                // onChange={handleCategoryChange}
              />
            </div>
            <button className="subcat-edit-button" type="submit">
             Create
            </button>
          </form>
        </div>
      </div>
    </>
  );
};

export default Category;
