import "./login.css";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import jwt_decode from "jwt-decode";

function Login() {
    // the login forms functionalty
  const [isSignup, setIsSignup] = useState(false);

  const handleButtonClick = () => {
    setIsSignup(!isSignup);
  };

  const containerStyle = {
    background: isSignup
      ? "linear-gradient(to bottom, rgb(56, 189, 149), rgb(28, 139, 106))"
      : "linear-gradient(to bottom, rgb(6, 108, 224), rgb(14, 48, 122))",
  };

  // the sign up function

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  let navigate = useNavigate();
  const [error, setError] = useState(null);

  const handleRegister = async (event) => {
    event.preventDefault();
    setError(null);

    try {
      const response = await fetch("https://cardsy.onrender.com/user/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password, email }),
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error);
      }

      const data = await response.json();
      sessionStorage.setItem("token", data.token);
      sessionStorage.setItem("Id", data.user);
      sessionStorage.setItem("role", data.role);

      // Decode the JWT token
      const decodedToken = jwt_decode(data.token);
      // console.log(decodedToken);

      alert("You have registered successfully");


      // if (data.role === "user") {
      //   navigate("/Home");
      // }

      window.location.href = "/Home";

      console.log("Registration successful");
    } catch (error) {
      setError(error.message);
      console.error(error);
       // Show error alert
       alert("Registration failed");
    }
  };



  return (
    <div className="container" style={containerStyle}>
      <div className="box-1">
        <div className="content-holder">
          <h2>Hello!</h2>
          <button className="button-1" onClick={handleButtonClick}>
            {isSignup ? "Login" : "Sign up"}
          </button>
        </div>
      </div>

      <div className="box-2">
        <div
          className="login-form-container"
          style={{ display: isSignup ? "none" : "block" }}
        >
          <h1>Login Form</h1>
          <input type="text" placeholder="Username" className="input-field" />
          <br />
          <input type="password" placeholder="Password" className="input-field" />
          <br />
          <button className="login-button" type="button">
            Login
          </button>
        </div>

        <form onSubmit={handleRegister}
          className="signup-form-container"
          style={{ display: isSignup ? "block" : "none" }}
        >
          <h1>Sign Up Form</h1>
          <input type="text" placeholder="Username" className="input-field" />
          <br />
          <input type="email" placeholder="Email" className="input-field" />
          <br />
          <input type="password" placeholder="Password" className="input-field" />
          <br />
          <button className="signup-button" type="button">
            Sign Up
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
