import React, { useState, useEffect } from "react";
import axios from "axios";
import Select from "react-select"; // Import react-select

const Menu = () => {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [menus, setMenus] = useState([]);
  const [menu, setMenu] = useState({
    menu: "",
    products: [],
    categories: [],
  });

  useEffect(() => {
    const fetchMenus = async () => {
      try {
        const response = await axios.get("http://localhost:5000/menu");
        setMenus(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:5000/product");
        setProducts(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    const fetchCategories = async () => {
      try {
        const response = await axios.get("http://localhost:5000/category");
        setCategories(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMenus();
    fetchCategories();
    fetchProducts();
  }, []);

  const handleMenuChange = (name, value) => {
    setMenu({ ...menu, [name]: value });
  };

  const handleCreateMenu = async (event) => {
    console.log(menu.menu)
    console.log(menu.products)
    console.log(menu.products)

    event.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/menu", {
        menu: menu.menu,
        products: menu.products.map((item) => item.value),
        categories: menu.categories.map((item) => item.value),
      });

      if (response.status === 201) {
        setMenu({
          menu: "",
          products: [],
          categories: [],
        });
      } else {
        console.error("Failed to create menu");
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <>
      <div className="category-table">
        <div className="list-category">
          <h3 className="list1">list of Menus</h3>

          {menus.map((item, i) => (
            <p key={i}>{item.menu}</p>
          ))}
        </div>
        <div className="subcat-form-container">
          <h1>Create Menu</h1>
          <form className="cat-edit-form2" onSubmit={handleCreateMenu}>
            <div className="username">
              <label className="About_username">Menu name:</label> <br />
              <input
                className="subcat-edit-input"
                type="text"
                id="username"
                placeholder="Menu name"
                name="menu"
                value={menu.menu}
                onChange={(e) => handleMenuChange("menu", e.target.value)}
              />
            </div>
            <div>
              <label className="About_username">Categories:</label>
              <Select
                id="category"
                name="categories"
                isMulti
                options={categories.map((category) => ({
                  value: category._id,
                  label: category.category,
                }))}
                value={menu.categoryId}
                onChange={(selectedOptions) =>
                  handleMenuChange(
                    "categoryId",
                    selectedOptions ? selectedOptions : []
                  )
                }
              />
            </div>
            <div>
              <label className="About_username">Products:</label>
              <Select
                id="product"
                name="products"
                isMulti
                options={products.map((product) => ({
                  value: product._id,
                  label: product.product,
                }))}
                value={menu.productId}
                onChange={(selectedOptions) =>
                  handleMenuChange(
                    "productId",
                    selectedOptions ? selectedOptions : []
                  )
                }
              />
            </div>

            <button className="subcat-edit-button" type="submit">
              Create
            </button>
          </form>
        </div>
      </div>
    </>
  );
};

export default Menu;
