import './App.css';
import React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

function App() {


  const data = [
  {
    id: 1,
    CustomerName: "John Doe",
    Status: "Pending",
    InvoiceDetails: [
      {
        ItemName: "Product A",
        Price: 10.99
      },
      {
        ItemName: "Product B",
        Price: 15.99
      }
    ]
  },
  {
    id: 2,
    CustomerName: "John Doe",
    Status: "Pending",
    InvoiceDetails: [
      {
        ItemName: "Product A",
        Price: 10.99
      },
      {
        ItemName: "Product B",
        Price: 15.99
      }
    ]
  },
  {
    id: 3,
    CustomerName: "John Doe",
    Status: "Pending",
    InvoiceDetails: [
      {
        ItemName: "Product A",
        Price: 10.99
      },
      {
        ItemName: "Product B",
        Price: 15.99
      }
    ]
  }

]



  return (
    <div className="App">
      <div className="cart-table-prodash">
            <Paper
              sx={{
                width: "75%",

                overflow: "hidden",
                marginLeft: "auto",
                marginRight: "auto",
                border: "#0B486A solid 1px",
              }}
            >
              <TableContainer sx={{ maxHeight: "600px" }}>
                <Table stickyHeader aria-label="sticky table">
                  <TableHead>
                    <TableRow>
                      {columns.map((column) => (
                        <TableCell
                          key={column.id}
                          align={column.align}
                          style={{ minWidth: column.minWidth }}
                        >
                          {column.label}
                        </TableCell>
                      ))}
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {data.map((product, i) => (
                      <TableRow key={i}>
                        <TableCell>
                          <button
                            className="cart-button-icon"
                            // onClick={() => handleDelete(product._id)}
                          >
                            <FontAwesomeIcon
                              // icon={faCircleXmark}
                              className="cart-Xicon"
                            />
                          </button>
                        </TableCell>
                        <TableCell className="cart-item-image">
                          <img  />
                        </TableCell>
                        <TableCell>{product.name}</TableCell>
                        <TableCell>{product.size}</TableCell>
                        <TableCell>{product.price}</TableCell>
                        <TableCell>{product.category.title}</TableCell>
                        <TableCell>{product.subcategory.title}</TableCell>
                        <TableCell>
                          <button
                            className="prodash-button"
                            // onClick={() => getProductById(product._id)}
                          >
                            <img className="prodash-icon" src={edit} alt="#" />
                          </button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Paper>
          </div>


    </div>
  );
}

export default App;
