import '../App.css';
import React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleXmark } from "@fortawesome/free-solid-svg-icons";
import edit from "../asset/icons8-create-64.png";
import axios from "axios";
import { useEffect, useState, useRef } from "react";
import close from '../asset/icons8-close-window-48.png';
import add from "../asset/icons8-add-new-64.png"

function Home() {
    const form = useRef();
    const [data , setData] = useState([]);
    const [editMode, setEditMode] = useState(false);
    const [addMode, setAddMode] = useState(false);
    const [invoice , setInvoice] = useState();
    const [customerName, setCustomerName] = useState('');
    const [status, setStatus] = useState('');
    const [invoiceDetails, setInvoiceDetails] = useState([
      { itemName: '', price: '' },
    ]);

    const handleInputChange = (index, e) => {
      const { name, value } = e.target;
      const updatedDetails = [...invoiceDetails];
      updatedDetails[index][name] = value;
      setInvoiceDetails(updatedDetails);
    };


    useEffect(() => {
        axios
          .get(`http://localhost:5000/invoice`)
          .then((response) => {
            setData(response.data);
          })
          .catch((error) => {
            console.log(error);
          });
      }, []);


      const handleDelete = async (id) => {
        try{
            await axios.delete(`http://localhost:5000/invoice/${id}`);
            setData(data);
            window.location.reload();

        }catch(error){

         console.log(error)
        }


      }

      const handleAdd = () => {
        setAddMode(true);
      };
      const handleClose = () => {
        setAddMode(false);
        setEditMode(false);
      };

      const handleAddItem = () => {
        setInvoiceDetails([...invoiceDetails, { itemName: '', price: '' }]);
      };

      const handleRemoveItem = (index) => {
        const updatedDetails = [...invoiceDetails];
        updatedDetails.splice(index, 1);
        setInvoiceDetails(updatedDetails);
      };

      const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = {
          CustomerName: customerName,
          Status: status,
          InvoiceDetails: invoiceDetails,
        };

        // Send formData to your API using axios or fetch
        // Reset form fields after successful submission
        try {
          const response = await axios.post(`http://localhost:5000/invoice`, {

           customerName,
           status,
           invoiceDetails

          });
          setCustomerName(response.data.customerName);
           setStatus(response.data.s);
           setInvoiceDetails(response.data);
          setAddMode(false);
           window.location.reload();
        }catch(error){
          console.error(error);
        }
      };

 const handleUpdate = async ()=> {

 }
 const getProductById = async (id) => {
  console.log(id);
  try {
    const response = await axios.get(
      `http://localhost:5000/invoice/${id}`
    );
    setInvoice(response.data);

    setEditMode(true);
    console.log("get by id", invoice);
  } catch (error) {
    console.error(error);
  }
};



const columns = [
  { id: "remove", label: " ", minWidth: 100 },
  { id: "product", label: "Invoice Number", minWidth: 100 },
  { id: "size", label: "Customer Name", minWidth: 100 },
  { id: "price", label: "Status", minWidth: 100 },
  { id: "edit", label: "Edit", minWidth: 100 },
];



  return (
    <div className="App">
      <div className="cart-table-prodash">
            <Paper
              sx={{
                width: "75%",

                overflow: "hidden",
                marginLeft: "auto",
                marginRight: "auto",
                border: "#0B486A solid 1px",
              }}
            >
              <TableContainer sx={{ maxHeight: "600px" }}>
                <Table stickyHeader aria-label="sticky table">
                  <TableHead>
                    <TableRow>
                      {columns.map((column) => (
                        <TableCell
                          key={column.id}
                          align={column.align}
                          style={{ minWidth: column.minWidth }}
                        >
                          {column.label}
                        </TableCell>
                      ))}
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {data.map((product, i) => (
                      <TableRow key={i}>
                        <TableCell>
                          <button
                            className="cart-button-icon"
                            onClick={() => handleDelete(product._id)}
                          >
                            <FontAwesomeIcon
                              icon={faCircleXmark}
                              className="cart-Xicon"
                            />
                          </button>
                        </TableCell>

                        <TableCell>{product._id}</TableCell>
                        <TableCell><a href={`/${product._id}`}>{product.CustomerName}</a></TableCell>
                        <TableCell>{product.Status}</TableCell>

                        <TableCell>
                          <button
                            className="prodash-button"
                            onClick={() => getProductById(product._id)}
                          >
                            <img className="prodash-icon" src={edit} alt="#" />
                          </button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Paper>
          </div>
          <div>
          <button className="prodash-button"
          onClick={handleAdd}
          >
                  <img src={add} alt="" className="prodash-icon" />
                </button>
          </div>
          <div className="class-form">
              {addMode && (
                <>
               <h2>Add Invoice</h2>
               <button className="prodash-button"
                  onClick={handleClose}
                  >
                    <img src={close} alt="" className="prodash-icon"  />
                  </button>
               <form onSubmit={handleSubmit}>
                 <div>
                   <label htmlFor="customerName">Customer Name:</label>
                   <input
                     type="text"
                     id="customerName"
                     name="customerName"
                     value={customerName}
                     onChange={(e) => setCustomerName(e.target.value)}
                   />
                 </div>
                 <div>
                   <label htmlFor="status">Status:</label>
                   <input
                     type="text"
                     id="status"
                     name="status"
                     value={status}
                     onChange={(e) => setStatus(e.target.value)}
                   />
                 </div>
                 <div>
                   <h3>Invoice Details:</h3>
                   {invoiceDetails.map((detail, index) => (
                     <div key={index}>
                       <input
                         type="text"
                         name="itemName"
                         placeholder="Item Name"
                         value={detail.itemName}
                         onChange={(e) => handleInputChange(index, e)}
                       />
                       <input
                         type="text"
                         name="price"
                         placeholder="Price"
                         value={detail.price}
                         onChange={(e) => handleInputChange(index, e)}
                       />
                       {index > 0 && (
                         <button type="button" onClick={() => handleRemoveItem(index)}>
                           Remove
                         </button>
                       )}
                     </div>
                   ))}
                   <button type="button" onClick={handleAddItem}>
                     Add Item
                   </button>
                 </div>
                 <button type="submit">Submit</button>
               </form>
               </>
              )}
              {editMode && (
                <form onSubmit={handleUpdate} ref={form}>
                  <input
                    className="class-input"
                    type="text"
                    placeholder="Class Name"
                    name="levelName"
                    // value={formData.levelName}
                    // onChange={handleChange}
                  ></input>
                   <input
                    className="class-input"
                    type="text"
                    placeholder="Class Name"
                    name="levelName"
                    // value={formData.levelName}
                    // onChange={handleChange}
                  ></input>
                   <input
                    className="class-input"
                    type="text"
                    placeholder="Class Name"
                    name="levelName"
                    // value={formData.levelName}
                    // onChange={handleChange}
                  ></input>
                   <input
                    className="class-input"
                    type="text"
                    placeholder="Class Name"
