import "./login.css";
import React from "react";

function login() {
    const signup = ()=>
{
    document.querySelector(".login-form-container").style.cssText = "display: none;";
    document.querySelector(".signup-form-container").style.cssText = "display: block;";
    document.querySelector(".container").style.cssText = "background: linear-gradient(to bottom, rgb(56, 189, 149),  rgb(28, 139, 106));";
    document.querySelector(".button-1").style.cssText = "display: none";
    document.querySelector(".button-2").style.cssText = "display: block";

};

const login = ()=>
{
    document.querySelector(".signup-form-container").style.cssText = "display: none;";
    document.querySelector(".login-form-container").style.cssText = "display: block;";
    document.querySelector(".container").style.cssText = "background: linear-gradient(to bottom, rgb(6, 108, 224),  rgb(14, 48, 122));";
    document.querySelector(".button-2").style.cssText = "display: none";
    document.querySelector(".button-1").style.cssText = "display: block";

}
  return (
    <>
      <div class="container">
        <div class="box-1">
          <div class="content-holder">
            <h2>Hello!</h2>

            <button class="button-1" onclick={signup()}>
              Sign up
            </button>
            <button class="button-2" onclick={login()}>
              Login
            </button>
          </div>
        </div>

        <div class="box-2">
          <div class="login-form-container">
            <h1>Login Form</h1>
            <input type="text" placeholder="Username" class="input-field" />
            <br></br>
            <input type="password" placeholder="Password" class="input-field" />
            <br></br>
            <button class="login-button" type="button">
              Login
            </button>
          </div>

          <div class="signup-form-container">
            <h1>Sign Up Form</h1>
            <input type="text" placeholder="Username" class="input-field" />
            <br></br>
            <input type="email" placeholder="Email" class="input-field" />
            <br></br>
            <input type="password" placeholder="Password" class="input-field" />
            <br></br>
            <button class="signup-button" type="button">
              Sign Up
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default login;
