import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import { faCircleXmark } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function MenuDetails() {
  const columns = [
    { id: "remove", label: " ", minWidth: 100 },
    { id: "images", label: "Subcategory", minWidth: 100 },
    { id: "product", label: "Category", minWidth: 100 },
    { id: "edit", label: "edit", minWidth: 100 },
  ];

  con
  return (
    <div>
      <h1>Menu Details</h1>
      <div>
        <h3>Menuname</h3>
        <div>
          <Paper
            sx={{
              width: "75%",
              overflow: "hidden",
              marginLeft: "auto",
              marginRight: "auto",
              border: "#0B486A solid 1px",
            }}
          >
            <TableContainer sx={{ maxHeight: "600px" }}>
              <Table stickyHeader aria-label="sticky table">
                <TableHead>
                  <TableRow>
                    {columns.map((column) => (
                      <TableCell
                        key={column.id}
                        align={column.align}
                        style={{ minWidth: column.minWidth }}
                      >
                        {column.label}
                      </TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {subcategories.map((product, i) => (
                    <TableRow key={i}>
                      <TableCell>
                        <button
                          className="cart-button-icon"
                          onClick={() => handleRemove(product._id)}
                        >
                          <FontAwesomeIcon
                            icon={faCircleXmark}
                            className="cart-Xicon"
                          />
                        </button>
                      </TableCell>
                      <TableCell>{product.title}</TableCell>
                      <TableCell>{product.category.title}</TableCell>
                      <TableCell>
                        <button
                          className="prodash-button"
                        //   onClick={() => getProductById(product._id)}
                        >
                           Edit Price </button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Paper>
        </div>
      </div>
    </div>
  );
}

export default MenuDetails;
