import '../App.css';
import React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleXmark } from "@fortawesome/free-solid-svg-icons";
import edit from "../asset/icons8-create-64.png";
import axios from "axios";
import { useEffect, useState, useRef } from "react";
import close from '../asset/icons8-close-window-48.png';
import add from "../asset/icons8-add-new-64.png"

function Home() {
    const form = useRef();
    const [data , setData] = useState([]);
    const [editMode, setEditMode] = useState(false);
    const [addMode, setAddMode] = useState(false);

    useEffect(() => {
        axios
          .get(`http://localhost:5000/invoice`)
          .then((response) => {
            setData(response.data);
          })
          .catch((error) => {
            console.log(error);
          });
      }, []);


      const handleDelete = async (id) => {
        try{
            await axios.delete(`http://localhost:5000/invoice/${id}`);
            setData(data);
            window.location.reload();

        }catch(error){

         console.log(error)
        }


      }
      const handleAdd = () => {
        setAddMode(true);
      };
      const handleAddItem = async () => {


      }
 const handleUpdate = async ()=> {

 }



const columns = [
  { id: "remove", label: " ", minWidth: 100 },
  { id: "product", label: "Invoice Number", minWidth: 100 },
  { id: "size", label: "Customer Name", minWidth: 100 },
  { id: "price", label: "Status", minWidth: 100 },
  { id: "edit", label: "Edit", minWidth: 100 },
];



  return (
    <div className="App">
      <div className="cart-table-prodash">
            <Paper
              sx={{
                width: "75%",

                overflow: "hidden",
                marginLeft: "auto",
                marginRight: "auto",
                border: "#0B486A solid 1px",
              }}
            >
              <TableContainer sx={{ maxHeight: "600px" }}>
                <Table stickyHeader aria-label="sticky table">
                  <TableHead>
                    <TableRow>
                      {columns.map((column) => (
                        <TableCell
                          key={column.id}
                          align={column.align}
                          style={{ minWidth: column.minWidth }}
                        >
                          {column.label}
                        </TableCell>
                      ))}
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {data.map((product, i) => (
                      <TableRow key={i}>
                        <TableCell>
                          <button
                            className="cart-button-icon"
                            onClick={() => handleDelete(product._id)}
                          >
                            <FontAwesomeIcon
                              icon={faCircleXmark}
                              className="cart-Xicon"
                            />
                          </button>
                        </TableCell>

                        <TableCell>{product._id}</TableCell>
                        <TableCell><a href={`/${product._id}`}>{product.CustomerName}</a></TableCell>
                        <TableCell>{product.Status}</TableCell>

                        <TableCell>
                          <button
                            className="prodash-button"
                            // onClick={() => getProductById(product._id)}
                          >
                            <img className="prodash-icon" src={edit} alt="#" />
                          </button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Paper>
          </div>
          <div>
          <button className="prodash-button"
          onClick={handleAdd}
          >
                  <img src={add} alt="" className="prodash-icon" />
                </button>
          </div>
          <div className="class-form">
              {addMode && (
                <form onSubmit={handleAddItem} ref={form}>
                  <input
                    className="class-input"
                    type="text"
                    placeholder="Class Name"
                    name="levelName"
                    // value={formData.levelName}
                    // onChange={handleChange}
                  ></input>
                  <input
                    className="class-input"
                    type="text"
                    placeholder="Capacity"
                    name="capacity"
                    // value={formData.capacity}
                    // onChange={handleChange}
                  ></input>
                    <input
                    className="class-input"
                    type="text"
                    placeholder="Capacity"
                    name="capacity"
                    // value={formData.capacity}
                    // onChange={handleChange}
                  ></input>
                    <input
                    className="class-input"
                    type="text"
                    placeholder="Capacity"
                    name="capacity"
                    // value={formData.capacity}
                    // onChange={handleChange}
                  ></input>
                  <button type="submit" className="class-buttonRR">
                    SAVE
                  </button>
                  <button className="prodash-button"
                  onClick={handleClose}
                  >
                    <img src={close} alt="" className="prodash-icon" />
                  </button>
                </form>
              )}
              {editMode && (
                <form onSubmit={handleUpdate} ref={form}>
                  <input
                    className="class-input"
                    type="text"
                    placeholder="Class Name"
                    name="levelName"
                    // value={formData.levelName}
                    // onChange={handleChange}
                  ></input>
                   <input
                    className="class-input"
                    type="text"
                    placeholder="Class Name"
                    name="levelName"
                    // value={formData.levelName}
                    // onChange={handleChange}
                  ></input>
                   <input
                    className="class-input"
                    type="text"
                    placeholder="Class Name"
                    name="levelName"
                    // value={formData.levelName}
                    // onChange={handleChange}
                  ></input>
                   <input
                    className="class-input"
                    type="text"
                    placeholder="Class Name"
                    name="levelName"
                    // value={formData.levelName}
                    // onChange={handleChange}
                  ></input>
                  <button type="submit" className="class-buttonR">
                    SAVE
                  </button>
                  <button className="class-button-close" 
                  // onClick={handleClose}
                  >
                    <img src={close} alt="" className="class-image" />
                  </button>
                </form>
              )}
            </div>

    </div>
  );
}

export default Home;
