  }

  return (
    <>
      <div>
        <Paper
          sx={{
            width: "75%",
            overflow: "hidden",
            marginLeft: "auto",
            marginRight: "auto",
            border: "#0B486A solid 1px",
          }}
        >
          <TableContainer sx={{ maxHeight: "600px" }}>
            <Table stickyHeader aria-label="sticky table">
              <TableHead>
                <TableRow>
                  {columns.map((column) => (
                    <TableCell
                      key={column.id}
                      align={column.align}
                      style={{ minWidth: column.minWidth }}
                    >
                      {column.label}
                    </TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {users?.map((user, i) => (
                  <TableRow key={i}>
                    <TableCell>{user.username}</TableCell>
                    <TableCell>{user.email}</TableCell>
                    <TableCell>
                      <div>
                        <button
                          type="submit"
                          className="prodash-button"
                            onClick={activateUser}
                        >
                          Activate User{" "}
                        </button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Paper>
      </div>
    </>
  );
}

export default UserList;
